import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import CafeContext from '../context/CafeContext';
import { naviItemsEN, naviItemsPT } from '../services/lists';

export default function App() {
  const [naviItems, setNaviItems] = useState([]);
  const { language, setLanguage } = useContext(CafeContext);

  const langChange = ({ target }) => {
    const { id } = target;
    if (id === 'PT-BTN') {
      return setLanguage('PT');
    }
    return setLanguage('EN');
  };

  useEffect(() => {
    if (language === 'PT') {
      return setNaviItems(naviItemsPT);
    }
    return setNaviItems(naviItemsEN);
  }, [language]);

  return (
    <header className="header-container">
      <div className="header-content w90">
        <span className="header-logo title-2 stk-3 white-100">Caf√©</span>
        <div className="header-controls-container">
          <navi className="header-navi-container">
            <ul className="header-navi-list">
              {naviItems.map((item, index) => <li className="header-navi-item" key={index}>{item}</li>)}
            </ul>
          </navi>
          <div className="header-langs">
            <button type="button" className="header-lag-btn btn" onClick={langChange} id="PT-BTN">PT</button>
            <button type="button" className="header-lag-btn btn" onClick={langChange} id="EN-BTN">EN</button>
          </div>
          <div className="header-login-BTN-container">
            <button type="button" className="header-login-btn btn" id="Login-BTN">
              <Link to="/login" className="header-link-login">Login</Link>
            </button>
          </div>
        </div>
      </div>
    </header>
  );
}
